import React, { useState}from 'react';
import axios from 'axios';
import './App.css';
import Main from './components/Main';
import Navbar from './components/Navbar';
import Footer from './components/Footer';

function App() {
  const [formData, setFormData] = useState({
    Ndehistoriaclinica: '',
    nombre: '',
    apellido: '',
    genero: '',
    Fechadenacimiento: '',
    Enfermedad: '',
    otrasenfermedades: '',
    tratamiento: '',
    genotipos: '',
  });

  const [resultados, setResultados] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = () => {
    // Envía los datos al servidor Django y realiza solicitudes a la API de PharmGKB.
    // ...

    // Actualiza los resultados y muestra la información.
    setResultados(resultadosData);
  };

  const handleDownloadPDF = () => {
    // Lógica para generar y descargar un PDF.
    // ...
  };

  return (
    <div className="App">
      <Navbar />
      <div className="main-content">
        <div className="row">
          <div className="column">
            <Main formData={formData} handleInputChange={handleInputChange} />
          </div>
          <div className="column column-center">
            <div className="buttons-container">
              <button onClick={handleSubmit}>Mostrar Resultados</button>
              <button onClick={handleDownloadPDF}>Descargar PDF</button>
            </div>
          </div>
          <div className="column">
            <div className="resultado-container">
                <h2>Resultado</h2>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}


export default App;

