import React, { useState }from 'react';
import axios from 'axios';
import './App.css';
import Main from './components/Main';
import Navbar from './components/Navbar';
import jsPDF from 'jspdf';

import {
  buscarAlelosGen,
  ID_CPIC_Farmaco,
  fenotipoSegunAlelos,
  urlGuia,
  recomendacionClinica,
  BuscarFarmacosRelacionadosGen,
} from './api';

function App() {
  const [formData, setFormData] = useState({
    Ndehistoriaclinica: '',
    nombre: '',
    apellido: '',
    genero: '',
    Fechadenacimiento: '',
    Enfermedad: '',
    otrasenfermedades: '',
    tratamiento: '',
    genotipos: '',
  });

  const [resultadosData, setResultadosData] = useState({
    Genotype: '',
    Phenotype: '',
    Population: '',
    Implications: '',
    Recommendation: '',
    OtherConsiderations: '',
    Classification: '',
  });


/*create a function that an API call for this Api url "https://api.cpicpgx.org/v1/allele?genesymbol=eq."+gen*/

  const [resultados, setResultados] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const [alelos, setAlelos] = useState([]);
  const [recomendaciones, setRecomendaciones] = useState([]);
  const [relacionesFarmacos, setRelacionesFarmacos] = useState([]);

  // Ejemplo de uso de la función buscarAlelosGen
  useEffect(() => {
    async function fetchAlelos() {
      try {
        const alelosGen = await buscarAlelosGen('CYP2C19'); // Reemplaza 'CYP2C19' con el gen deseado
        setAlelos(alelosGen);
      } catch (error) {
        console.error('Error al obtener los alelos:', error);
      }
    }

    fetchAlelos();
  }, []);

  // Ejemplo de uso de la función recomendacionClinica
  useEffect(() => {
    async function fetchRecomendaciones() {
      try {
        const recomendacionesFarma = await recomendacionClinica('CYP2C19', 'alelo1', 'alelo2', 'NombreDelFarmaco'); // Reemplaza con los valores adecuados
        setRecomendaciones(recomendacionesFarma);
      } catch (error) {
        console.error('Error al obtener las recomendaciones:', error);
      }
    }

    fetchRecomendaciones();
  }, []);

  // Ejemplo de uso de la función BuscarFarmacosRelacionadosGen
  useEffect(() => {
    async function fetchRelacionesFarmacos() {
      try {
        const relacionesFarmacosGen = await BuscarFarmacosRelacionadosGen('CYP2C19'); // Reemplaza 'CYP2C19' con el gen deseado
        setRelacionesFarmacos(relacionesFarmacosGen);
      } catch (error) {
        console.error('Error al obtener las relaciones con otros fármacos:', error);
      }
    }

    fetchRelacionesFarmacos();
  }, []);

  const fetchData = async (geneSymbol) => {
    const apiUrl = `https://api.cpicpgx.org/v1/allele?genesymbol=eq.${geneSymbol}`;
    try {
      const response = await fetch(apiUrl);
      const data = await response.json();
      setResultadosData({
        Genotype: data.genotype,
        Phenotype: data.phenotype,
        Population: data.population,
        Implications: data.implications,
        Recommendation: data.recommendation,
        OtherConsiderations: data.otherConsiderations,
        Classification: data.classification,
      });
      setResultados(data);
      console.log(data);
    } catch (error) {
      console.error(error);
      console.log(error);
    }
  };
  
  const handleDownloadPDF = (formData) => {
    if (formData) {
      // Crear un nuevo objeto jsPDF
      const pdf = new jsPDF();
  
      // Obtener la información del paciente desde el estado formData
      const pacienteInfo = {
        'Nº Historia clínica': formData.Ndehistoriaclinica,
        'Nombre': formData.nombre,
        'Apellido': formData.apellido,
        'Género': formData.genero,
        'Fecha de nacimiento': formData.Fechadenacimiento,
        'Enfermedad Actual': formData.Enfermedad,
        'Otras Enfermedades': formData.otrasenfermedades,
        'Tratamiento habitual': formData.tratamiento,
        'Genotipos': formData.genotipos,
        'Alelo 1': formData.alelo1 || 'N/A',
        'Alelo 2': formData.alelo2 || 'N/A',
      };
  
      // Verificar si resultados está definido y agregarlo al contenido del PDF
      if (resultados) {
        pacienteInfo['Resultado'] = resultados;
      }
  
      // Crear el contenido del PDF
      let content = 'Información del paciente:\n\n';
      for (const key in pacienteInfo) {
        const value = pacienteInfo[key];
        if (value !== undefined) {
          content += `${key}: ${value}\n`;
        }
      }
  
      // Agregar el contenido al PDF
      pdf.text(content, 10, 10);
  
      // Guardar el PDF con un nombre deseado (por ejemplo, "informe.pdf")
      pdf.save('informe.pdf');
    }
  };
  

  return (
    <div className="App">
      <Navbar />
      <div className="Todo">
      <div className="main-content">
        <div className="row">
          <div className="column">
            <Main formData={formData} handleInputChange={handleInputChange} />
          </div>
          <div className="column column-center">
            <div className="buttons-container">
              <button id="descargarBtn" onClick={() => fetchData('geneId')}>Mostrar Resultados</button>
              <button type="submit1" onClick={() => handleDownloadPDF(formData)}>Descargar PDF</button>
            </div>
          </div>
          <div className="column resultado-column">
            <div className="resultado-container">
                <h2>RESULTADO</h2>

                <div className="result-details">
                  <p className="result-section">
                    <strong>Genotipo:</strong> {resultadosData.Genotype}
                  </p>
                  <p className="result-section">
                    <strong>Fenotipo:</strong> {resultadosData.Phenotype}
                  </p>
                  <p className="result-section">
                    <strong>Populación:</strong> {resultadosData.Population}
                  </p>
                  <p className="result-section">
                    <strong>Implicaciones:</strong> {resultadosData.Implications}
                  </p>
                  <p className="result-section">
                    <strong>Recomendación:</strong> {resultadosData.Recommendation}
                  </p>
                  <p className="result-section">
                    <strong>Otras Consideraciones:</strong> {resultadosData.OtherConsiderations}
                  </p>
                  <p className="result-section">
                    <strong>Clasificación:</strong> {resultadosData.Classification}
                  </p>
                </div>
            </div>
          </div>
        </div>
      </div>
      </div>
    </div>
  );
}


export default App;
