import React, { useState} from 'react';
import './Main.css';
import axios from 'axios';


function Main() {
  const [formData, setFormData] = useState({
    Ndehistoriaclinica: '',
    nombre: '',
    apellido: '',
    genero: '',
    Fechadenacimiento: '',
    Enfermedad: '',
    otrasenfermedades: '',
    tratamiento: '',
    genotipos: '',
  });

  const [resultados, setResultados] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = () => {
    // Envía los datos al servidor Django
    axios
      .post('/api/crear_paciente/', formData)
      .then(response => {
        // Lógica de manejo de respuesta de Django

        // Luego, haz una solicitud a la API de PharmGKB con los datos del paciente.
        axios.get('https://api.pharmgkb.org/v1/endpoint-aqui', {
          headers: {
            'Api-Key': 'TU_CLAVE_DE_API',
          },
          params: {
            // Parámetros específicos de la API de PharmGKB
          },
        })
        .then(pharmgkbResponse => {
          // Procesa la respuesta de PharmGKB y actualiza los resultados.
          setResultados(pharmgkbResponse.data);

          // Puedes también mostrar el resultado aquí o en otro lugar en la página.
        })
        .catch(error => {
          console.error('Error al consultar la API de PharmGKB:', error);
          // Maneja errores aquí
        });
      })
      .catch(error => {
        console.error('Error al enviar datos a Django:', error);
        // Maneja errores aquí
      });
  };

  return (
    <div className="patient-info-form">
      <h2>Información del Paciente</h2>

      <form onSubmit={handleSubmit}>

        <div className="form-group">
            <label htmlFor="Ndehistoriaclinica">Nº Historia clínica</label>
            <input
              type="number"
              id="Ndehistoriaclinica"
              name="Ndehistoriaclinica"
              placeholder="Nº de la historia clinica"
              value={formData.nombrePaciente}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="nombre">Nombre</label>
            <input
              type="text"
              id="nombre"
              name="nombre"
              placeholder="Nombre del Paciente"
              value={formData.nombre}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="apellido">Apellido</label>
            <input
              type="text"
              id="apellido"
              name="apellido"
              placeholder="Apellido del Paciente"
              value={formData.apellido}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="genero">Género</label>
            <select
              id="genero"
              name="genero"
              value={formData.genero}
              onChange={handleInputChange}
              required
            >
              <option value="">Seleccionar Género</option>
              <option value="Masculino">Masculino</option>
              <option value="Femenino">Femenino</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="Fechadenacimiento">Fecha de nacimiento</label>
            <input
              type="date"
              id="Fechadenacimiento"
              name="Fechadenacimiento"
              placeholder="Fechadenacimiento"
              value={formData.Fechadenacimiento}
              onChange={handleInputChange}
              required
            />
          </div>


          <div className="form-group">
            <label htmlFor="Enfermedad">Enfermedad Actual</label>
            <select
              id="Enfermedad"
              name="Enfermedad"
              value={formData.Enfermedad}
              onChange={handleInputChange}
              required
            >
              <option value="">Selecciona la enfermedad</option>
              <option value="Cardiovascular">Cardiovascular</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="otrasenfermedades">Otras Enfermedades</label>
            <input
              type="text"
              id="otrasenfermedades"
              name="otrasenfermedades"
              placeholder="Otras Enfermedades"
              value={formData.otrasenfermedades}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="tratamiento">Tratamiento habitual</label>
            <select
              id="tratamiento"
              name="tratamiento"
              value={formData.tratamiento}
              onChange={handleInputChange}
              required
            >
              <option value="">Selecciona el tratamiento</option>
              <option value="Clopidogrel">Clopidogrel</option>
              <option value="Simvastatina">Simvastatina</option>
              <option value="Atorvastatina">Atorvastatina</option>
            </select>
          </div>


          <div className="form-group">
            <label htmlFor="genotipos">Genotipos</label>
            <select
              id="genotipos"
              name="genotipos"
              value={formData.genotipos}
              onChange={handleInputChange}
              required
            >
              <option value="">Selecciona un Genotipo</option>
              <option value="CYP2C19">CYP2C19</option>
              <option value="SLCO1B1">SLCO1B1</option>
            </select>
          </div>

          <div className="column column-center">
            <div className="buttons-container">
              <button type="submit">Mostrar Resultados</button>
              <button id="descargarBtn">Descargar PDF</button>
            </div>
          </div>
      </form>

    </div>
  );
}

export default Main;
