import React, { useState, useEffect}from 'react';
import axios from 'axios';
import './App.css';
import Main from './components/Main';
import Navbar from './components/Navbar';
import jsPDF from 'jspdf';

import {
  buscarAlelosGen,
  ID_CPIC_Farmaco,
  fenotipoSegunAlelos,
  urlGuia,
  recomendacionClinica,
  BuscarFarmacosRelacionadosGen,
} from './api';

function App() {
  const [formData, setFormData] = useState({
    Ndehistoriaclinica: '',
    nombre: '',
    apellido: '',
    genero: '',
    Fechadenacimiento: '',
    Enfermedad: '',
    otrasenfermedades: '',
    tratamiento: '',
    genotipos: '',
    alelo1: '',
    alelo2: '',
  });

  const [resultadosData, setResultadosData] = useState({
    Genotype: '',
    Phenotype: '',
    Population: '',
    Implications: '',
    Recommendation: '',
    OtherConsiderations: '',
    Classification: '',
  });


  const [alelos, setAlelos] = useState([]);
  const [recomendaciones, setRecomendaciones] = useState([]);
  const [relacionesFarmacos, setRelacionesFarmacos] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Ejemplo de uso de la función buscarAlelosGen
  useEffect(() => {
    async function fetchAlelos() {
      try {
        const alelosGen = await buscarAlelosGen('CYP2C19'); // Reemplaza 'CYP2C19' con el gen deseado
        setAlelos(alelosGen);
      } catch (error) {
        console.error('Error al obtener los alelos:', error);
      }
    }

    fetchAlelos();
  }, []);

  useEffect(() => {
    const obtenerDatos = async () => {
      try {
        // Aquí debes reemplazar 'CYP2C19' con el gen deseado
        const alelosGen = await buscarAlelosGen('CYP2C19');
        setAlelos(alelosGen);

        const recomendacionesFarma = await recomendacionClinica(formData.genotipos, formData.alelo1, formData.alelo2, formData.tratamiento);
        setRecomendaciones(recomendacionesFarma);

        // Aquí debes reemplazar 'CYP2C19' con el gen deseado
        const relacionesFarmacosGen = await BuscarFarmacosRelacionadosGen('CYP2C19');
        setRelacionesFarmacos(relacionesFarmacosGen);
      } catch (error) {
        console.error('Error al obtener los datos:', error);
      }
    };

    obtenerDatos();
  }, [formData.genotipos, formData.alelo1, formData.alelo2, formData.tratamiento]); // Dependencias para evitar llamadas innecesarias
  
  const handleDownloadPDF = () => {
    if (Object.values(formData).every(value => value)) { // Asegurar que todos los campos están llenos
      const pdf = new jsPDF();
      const pacienteInfo = {
        'Nº Historia clínica': formData.Ndehistoriaclinica,
        'Nombre': formData.nombre,
        'Apellido': formData.apellido,
        'Género': formData.genero,
        'Fecha de nacimiento': formData.Fechadenacimiento,
        'Enfermedad Actual': formData.Enfermedad,
        'Otras Enfermedades': formData.otrasenfermedades,
        'Tratamiento habitual': formData.tratamiento,
        'Genotipos': formData.genotipos,
        'Alelo 1': formData.alelo1 || 'N/A',
        'Alelo 2': formData.alelo2 || 'N/A',
        ...resultadosData, // Agregar los resultados al PDF
      };

      let content = 'Información del paciente:\n\n';
      for (const [key, value] of Object.entries(pacienteInfo)) {
        content += `${key}: ${value}\n`;
      }

      pdf.text(content, 10, 10);
      pdf.save('informe.pdf');
    } else {
      alert('Por favor, completa todos los campos del formulario antes de descargar el PDF.'); // Alerta para el usuario
    }
  };
  

  return (
    <div className="App">
      <Navbar />
      <div className="Todo">
      <div className="main-content">
        <div className="row">
          <div className="column">
            <Main formData={formData} handleInputChange={handleInputChange} />
          </div>
          <div className="column column-center">
            <div className="buttons-container">
            <button type="button" onClick={obtenerResultados}>Mostrar Resultados</button>
              <button type="button" onClick={() => handleDownloadPDF(formData)}>Descargar PDF</button>
            </div>
          </div>
          <div className="column resultado-column">
            <div className="resultado-container">
                <h2>RESULTADO</h2>

                <div className="result-details">
                  <p className="result-section">
                    <strong>Genotipo:</strong> {resultadosData.Genotype}
                  </p>
                  <p className="result-section">
                    <strong>Fenotipo:</strong> {resultadosData.Phenotype}
                  </p>
                  <p className="result-section">
                    <strong>Populación:</strong> {resultadosData.Population}
                  </p>
                  <p className="result-section">
                    <strong>Implicaciones:</strong> {resultadosData.Implications}
                  </p>
                  <p className="result-section">
                    <strong>Recomendación:</strong> {resultadosData.Recommendation}
                  </p>
                  <p className="result-section">
                    <strong>Otras Consideraciones:</strong> {resultadosData.OtherConsiderations}
                  </p>
                  <p className="result-section">
                    <strong>Clasificación:</strong> {resultadosData.Classification}
                  </p>
                </div>
            </div>
          </div>
        </div>
      </div>
      </div>
    </div>
  );
}


export default App;
